import { createSlice } from "@reduxjs/toolkit";

const initialState = { 
    cartItems: []
};

const cartSlice = createSlice({
    name: 'cart',
    initialState: initialState,
    reducers: {
        increment(state,action) {
            let index = state.cartItems.find(x => x.id === action.payload);
            if(!index)
            {
                state.cartItems.push(action.payload.item);
            }
            else {
                state.cartItems[index].quantity++;
            }
        },
        decrement(state,action){
            let index = state.cartItems.find(x => x.id === action.payload.id);
            if(!index)
            {
                //ERRORE non deve succedere
                console.log("ERRORE - oggetto da rimuovere non trovato");
            }
            else {
                let actualItem = state.cartItems[index];
                if(actualItem.quantity === 1)
                {
                    state.cartItems.splice(index);
                }
                else {
                    state.cartItems[index].quantity--;
                }
            }
        }
    }
});

export default cartSlice;
export const cartActions = cartSlice.actions;
