import { createSlice } from "@reduxjs/toolkit";

const initialState = { 
    cartItems: [{ title: 'Test Item', quantity: 3, total: 18, price: 6, id: 0 },
    { title: 'Test Item 2', quantity: 2, total: 10, price: 5, id: 1}]
};

const cartSlice = createSlice({
    name: 'cart',
    initialState: initialState,
    reducers: {
        increment(state,action) {
            let foundItem = state.cartItems.find(x => x.id === action.payload.id);
            if(!foundItem)
            {
                state.cartItems.push(action.payload);
            }
            else {
                foundItem.quantity++;
            }
        },
        decrement(state,action){
            let index = state.cartItems.indexOf(x => x.id === action.payload);
            if(!index)
            {
                //ERRORE non deve succedere
                console.log("ERRORE - oggetto da rimuovere non trovato");
            }
            else {
                let foundItem = state.cartItems[index];
                if(foundItem.quantity === 1)
                {
                    state.cartItems.splice(index);
                }
                else {
                    foundItem.quantity--;
                }
            }
        }
    }
});

export default cartSlice;
export const cartActions = cartSlice.actions;
