import { useSelector } from 'react-redux';
import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';

function App() {
  var totalItems = useSelector(state => state.cart.itemCount);
  var totalItems = useSelector(state => state.cart.showCart);

  return (
    <Layout>
      {totalItems > 0 && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
